import React from "react";
export default function Textform(props) {
  return (
    <div>
      <h1>{props.heading}</h1>
      <div class="mb-3">
        <label for="mybox" class="form-label">
          Text
        </label>
        <textarea
          class="form-control"
          placeholder="Leave a text here"
          id="mybox"
          rows="8"
        ></textarea>
      </div>
    </div>
  );
}
