import React, { useState } from "react";
export default function Textform(props) {
  const handleclick = () => {
    // console.log("upper case wase clicked");
    let newText = text.toUpperCase();
    setText(newText);
  };
  const handleLower = () => {
    // console.log("upper case wase clicked");
    let newText = text.toLowerCase();
    setText(newText);
  };
  const handleonchange = (event) => {
    // console.log("handle onchange was clicked");
    setText(event.target.value);
  };
  const [text, setText] = useState("");
  return (
    <>
      <div>
        <h1>{props.heading}</h1>
        <div className="mb-3">
          <textarea
            className="form-control"
            placeholder="Leave a text here"
            id="mybox"
            rows="8"
            value={text}
            onChange={handleonchange}
          ></textarea>
          <button className="btn btn-primary mt-2 mx-2" onClick={handleclick}>
            Convert To UpperCase
          </button>
          <button className="btn btn-success mt-2" onClick={handleLower}>
            Convert To LowerCase
          </button>
        </div>
      </div>
      <div className="container my-3">
        <h1>Your Text Summary</h1>
        <p>
          {text.split(" ").length} words and {text.length} characters
        </p>
        <p>{0.008 * text.split(" ").length} Minutes read</p>
        <h3>Preview</h3>
        <p>{text}</p>
      </div>
    </>
  );
  console.clear();
}
